import React, { Component } from "react";
// import API from "../utils/API";
// import Card from "../components/Card";
// import Alert from "../components/Alert";


// import Row from "../components/Row";
// import Col from "../components/Col";
import FriendCard from "../components/FriendCard";

// import Title from "../components/Title";
import data from "../friends.json";
import Container from 'react-bootstrap/Container'
// import ClickItem from "../ClickItem";
import Jumbotron from 'react-bootstrap/Jumbotron'
// import Image from 'react-bootstrap/Image'
import Card from 'react-bootstrap/Card'
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'

class clickyGame extends Component {
  state = {
    data,
    score: 0,
    topScore: 0
  };

  componentDidMount() {
    this.setState({ data: this.shuffleData(this.state.data) });
  }



  resetData = data => {
    const resetData = data.map(item => ({ ...item, clicked: false }));
    return this.shuffleData(resetData);
  };

  shuffleData = data => {
    let i = data.length - 1;
    while (i > 0) {
      const j = Math.floor(Math.random() * (i + 1));
      const temp = data[i];
      data[i] = data[j];
      data[j] = temp;
      i--;
    }
    return data;
  };

  handleItemClick = id => {
    let guessedCorrectly = false;
    const newData = this.state.data.map(item => {
      const newItem = { ...item };
      if (newItem.id === id) {
        if (!newItem.clicked) {
          newItem.clicked = true;
          guessedCorrectly = true;
        }
      }
      return newItem;
    });
    guessedCorrectly
      ? this.handleCorrectGuess(newData)
      : this.handleIncorrectGuess(newData);
  };

  handleCorrectGuess = newData => {
    const { topScore, score } = this.state;
    const newScore = score + 1;
    const newTopScore = Math.max(newScore, topScore);

    this.setState({
      data: this.shuffleData(newData),
      score: newScore,
      topScore: newTopScore
    });
  };

  handleIncorrectGuess = data => {
    this.setState({
      data: this.resetData(data),
      score: 0
    });
  };



  render() {
    return (
      <div>
        <Row>
        <Col md={12}>
        <Card className="bg-light text-black bold">
        <Card.Header className="text-center">Clicky-Game</Card.Header>

          <Card.Img src="https://cl.goliath.com/image/upload/t_tn,f_auto,q_auto,$h_375,$w_1050/go/2016/04/Looney-tunes-characters-the-free-542314-1050x375.jpg" alt="Card image"  />

          <Card.Footer className="text-center">Click on an image to earn points, but don't click on any more than once! </Card.Footer>
        </Card>
        </Col>
       </Row>
        <Container style={{ marginTop: 30 }}>

          {this.state.data.map(item => (
            <FriendCard
              id={item.id}
              key={item.id}
              name={item.name}
              image={item.image}

            />
          ))}
        </Container>

      </div>
    );
  }
}

export default clickyGame;
